def connectionWeb3():
    bsc = "https://bsc-dataseed.binance.org/"
    web3 = Web3(HTTPProvider(bsc))
    return web3


def transferBNB(conn, fromAddress, toAddress, amountBNB):
    nonce = conn.eth.getTransactionCount(fromAddress)
    if conn.isConnected():
        tx = {
            'nonce': nonce,
            'to': toAddress,
            'value': conn.toWei(amountBNB, 'ether'),
            'gas': 21000,
            'gasPrice': conn.toWei('5', 'gwei')
        }
    signed_tx = conn.eth.account.signTransaction(tx, getPrivateKey(fromAddress))
    tx_hash = conn.eth.sendRawTransaction(signed_tx.rawTransaction)
    tx_hash_hex = conn.toHex(tx_hash)
    print("TxId: " + tx_hash_hex)
    return tx_hash_hex


def getPrivateKey(account):
    return secretsManager.getSecretFromAWS(account)['private_key']

class contractInteraction:

    def __init__(self, conn, contactAddress, abi):
        self.contract = conn.eth.contract(address=contactAddress, abi=abi)
        self.conn = conn
        self.contractAddress = contactAddress
        self.contractAbi = abi

    def getTotalSupply(self):
        return self.contract.functions.totalSupply().call()

    def getName(self):
        return self.contract.functions.name().call()

    def getSymbol(self):
        return self.contract.functions.symbol().call()

    def getBalanceOf(self, address):
        return self.contract.functions.balanceOf(address).call()

    def convertToGweiFormat(self, value):
        return self.conn.fromWei(value, 'gwei')

    def humanReadable(self, value):
        return self.conn.fromWei(value, 'ether')

    def transfer(self, fromAddress, toAddress, amount):
        nonce = self.conn.eth.getTransactionCount(fromAddress)
        token_tx = self.contract.functions.transfer(toAddress, amount).buildTransaction(
            {
                'chainId': 56,
                'gas': 80000,
                'gasPrice': self.conn.toWei('5', 'gwei'),
                'nonce': nonce
            }
        )
        signed_tx = self.conn.eth.account.signTransaction(token_tx, getPrivateKey(fromAddress))
        tx_hash = self.conn.eth.sendRawTransaction(signed_tx.rawTransaction)
        tx_hash_hex = self.conn.toHex(tx_hash)
        print("Transfer TxId: " + tx_hash_hex)
        return tx_hash_hex

